package com.yandex.xplat.common


public enum class PlatformType(val value: String) {
    android("android"),
    electron("electron"),
    ios("ios"),
    touch("touch"),
    ;
    override fun toString(): String = value
}
public fun platformToClient(platform: Platform): String {
    when (platform.type) {
        PlatformType.android -> {
            return if (platform.isTablet) "apad" else "aphone"
        }
        PlatformType.ios -> {
            return if (platform.isTablet) "ipad" else "iphone"
        }
        PlatformType.electron -> {
            return "unknown"
        }
        PlatformType.touch -> {
            return "unknown"
        }
    }
}

public interface Platform {
    val type: PlatformType
    val isTablet: Boolean
}

