package com.yandex.xplat.common


public fun <T> resultExecuteSequentially(resultFactories: YSArray<() -> Result<T>>): Result<YSArray<T>> {
    val values: YSArray<T> = mutableListOf()
    for (resultFactory in resultFactories) {
        val result = resultFactory()
        if (result.isValue()) {
            values.add(result.getValue())
        } else {
            return resultError(result.getError())
        }
    }
    return Result(values, null)
}

public fun <T> toPromise(result: Result<T>): XPromise<T> {
    return if (result.isValue()) resolve(result.getValue()) else reject(result.getError())
}

