package com.yandex.xplat.common


public fun nullIfEmptyString(value: String?): String? {
    return if (value == "") null else value
}

public fun emptyStringIfNull(value: String?): String {
    return if (value == null) "" else value
}

public fun isStringNullOrEmpty(value: String?): Boolean {
    return value == null || value!!.length == 0
}

public fun quote(value: String): String {
    return "\"" + value + "\""
}

public fun stringReplaceAll(value: String, substr: String, newSubstr: String): String {
    return value.split(substr).joinToString(newSubstr)
}

public fun removeNewlines(value: String): String {
    return stringReplaceAll(value, "\n", " ")
}

