package com.yandex.xplat.yandex.pay

import com.yandex.xplat.common.*
import com.yandex.xplat.eventus.common.*

public fun maskCardNumberWithBin(cardNumber: String): String {
    val bin = cardNumber.slice(0, 6)
    val unmaskTailLength: Int = if (cardNumber.length < 14) 2 else 4
    val unmaskedTail = cardNumber.slice(-unmaskTailLength)
    return bin + unmaskedTail.padStart(cardNumber.length - bin.length, "*")
}

